        square.style.width = `${20 + size}px`;
        square.style.height = `${20 + size}px`;
        square.style.top = Math.random() * square.offsetHeight + `px`;
        square.style.left = Math.random() * square.offsetWidth + `px`;
        sectionBanner.appendChild(square)

        setTimeout(() => {
            square.remove()
        }, 5000)

    }

    useEffect(() => {
        setInterval(createSquare, 150)
      }, [])

    return <>
        <Header />
        <main className="main">
