import React from 'react';
import {useState} from "react";
import { allNavSiteLinks } from '../const/const';
import Logo from '../logo/logo';
import Navbar from 'react-bootstrap/ProgressBar';

const NavigationSite = () => {
    const defaultActiveLink = ``;
    const [activeLink, setActiveLink] = useState(defaultActiveLink);
    const handleActiveLink = (evt) => {
        setActiveLink(evt.target.dataset.link);
    };
    return <>
        <nav className="navbar navbar-default header__nav">
            <Logo />
            <ul className="list nav navbar-nav site-list">
                {allNavSiteLinks.map((siteLink) => {
                return <li key={siteLink} className="nav-item  site-list__item">
                    <a href="!#" onClick={handleActiveLink} className={`site-list__link ${siteLink === activeLink ? `site-list__link--active` : ``}`} data-link={siteLink} >{siteLink}</a>
                </li>;
                })}
            </ul>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation"></button>
        </nav>
    </>
}

export default NavigationSite;
