import React, { useState} from 'react';

const Contact = () => {
    const initialFormState = {
        user: ``,
        email: ``,
        comment: ``,
    };
    const [currentFormState, setCurrentFormState] = useState({...initialFormState});
    const [isCheckboxChecked, setIsCheckboxChecked] = useState(true);
    const [isInputError, setIsInputError] = useState({
        isUserError: false,
        isEmail: false,
        isCommentError: false,
        isCheckBoxError: false
    });
    const checkInputContent = () => {
        const regex = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (!currentFormState.user) {
            setIsInputError({
                ...isInputError,
                isUserError: true
            });
        } else {
            setIsInputError({
                ...isInputError,
                isUserError: false
            });
        }

        if (!currentFormState.email || regex.test(currentFormState.email) === false) {
            setIsInputError({
                ...isInputError,
                isEmail: true
            });
        } else {
            setIsInputError({
                ...isInputError,
                isEmail: false
            });
        }

        if (!currentFormState.comment) {
            setIsInputError({
                ...isInputError,
                isCommentError: true
            });
        } else {
            setIsInputError({
                ...isInputError,
                isCommentError: false
            });
        }

        if (isCheckboxChecked === false) {
            setIsInputError({
                ...isInputError,
                isCheckBoxError: true
            });
        } else {
            setIsInputError({
                ...isInputError,
                isCheckBoxError: false
            });
        }

      };
    // const formRef = useRef();
    // const validateForm = () => {

    // }

    const handleInputChange = (evt) => {
        console.log(`evt.target.name`, evt.target.name)
        setCurrentFormState({
          ...currentFormState,
          [evt.target.name]: evt.target.value,
        });
      };
    const handleSubmit = (evt) => {
        evt.preventDefault();
        // checkInputContent();
        console.log(`currentFormState`, currentFormState)
        console.log(`isInputError`, isInputError)
        // if (currentFormState.user.length !== 0 && currentFormState.comment.length !== 0) {
        //     // onSubmitFormReview(currentFormState);
        //     setCurrentFormState({
        //       ...initialFormState,
        //     });
        // }
    }
    return <>
        <section className="contact" id="contacts">

            <h2 className="title contact__title">Contact me</h2>
            <p className="contact__slogan">Have a question or want to work together? Send me a message and let's start your project right now!</p>
            <div ref={formRef} className="contact__wrapper">
                <form action="#" className="container-site contact__form contact-form" onSubmit={handleSubmit}>
                    <div className="list contact__list contact-form">
                        <div className="form-group contact-form__item">
                            <label htmlFor="name"></label>
                            <input 
                                type="text" 
                                className={`form-control contact-form__input ${isInputError.isUserError ? `contact-form__input--error` : ``}`} 
                                id="name" 
                                autoFocus={true}
                                // placeholder="Your name"
                                value={currentFormState.user}
                                onChange={handleInputChange}
                            >
                            </input>
                        </div>
                        <div className="form-group contact-form__item">
                            <label htmlFor="email"></label>
                            <input 
                                type="email" 
                                className={`form-control contact-form__input ${isInputError.isEmail ? `contact-form__input--error` : ``}`} 
                                id="email"
                                // placeholder="Your email"
                                value={currentFormState.email}
                                onChange={handleInputChange}
                            >
                            </input>
                        </div>
                        <div className="form-group contact-form__item">
                            <label htmlFor="message"></label>
                            <textarea 
                                className={`contact-form__textarea ${isInputError.isCommentError ? `contact-form__textarea--error` : ``} form-control `}
                                id="message"
                                // defaultValue="Leave me your message"
                                value={currentFormState.comment}
                                onChange={handleInputChange}
                            >
                            </textarea>
                        </div>   
                        <div className="form-group contact-form__item">
                            <input defaultChecked={isCheckboxChecked} type="checkbox" className="contact-form__checkbox" id="agreement"></input>
                            <label htmlFor="agreement" className="contact-form__checkbox-label">I agree to the assessment, use and processing of my personal data.</label>
                        </div>
                    </div>  
                    <button type="submit" className="button contact-form__button">Send me a mesage</button> 
                </form>
            </div>
      </section>
    </>
}

export default Contact;
