import React, {useRef, useEffect, useState} from 'react';
import Parallax from 'parallax-js'
import Contact from '../contacts/contsct';
import Footer from '../footer/footer';
import Header from '../header/header';
import ProgressBar from 'react-bootstrap/ProgressBar';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from "jquery";
import NavigationSite from '../navigation-site/navigation-site';
import About from '../about/about';
// import { CircularProgressbar } from 'react-circular-progressbar';


// import 'react-circular-progressbar/dist/styles.css';

const MainPage = () => {
    const [count, setCount] = useState(0);

    const sceneEl = useRef(null);
    const diagramEl = useRef(null);


    // useEffect(() => {
    //     const parallaxInstance = new Parallax(sceneEl.current, {
    //       relativeInput: true,
    //       hoverOnly: true
    //     })

    //     parallaxInstance.enable();

    //     return () => parallaxInstance.disable();
    //   }, [])
    const createCounter = () => {
        // let count = 0;
        const setFrame = () => {
            setCount(count + 1);
            console.log(`count`, count)
            if (count === 101) {
                clearInterval(runCounter)
            } else {
                return null
            }

        }
        const runCounter = setInterval(setFrame, 50);

    }

    // const figureColors = [
    //     `#3c1874`,
    //     `#DE354C`,
    //     `#F3F3F3`,
    //     `radial-gradient(#de354c, #642493)`,
    //     `radial-gradient(#eeaeca, #94bbe9)`,
    // ]
    // const createSquare = () => {
    //     const sectionBanner = document.querySelector(`.banners`)
    //     const square = document.createElement(`span`)
    //     const size = Math.random() * 50;
    //     square.style.width = `${20 + size}px`;
    //     square.style.height = `${20 + size}px`;
    //     square.style.top = Math.random() * sectionBanner.offsetHeight + `px`;
    //     square.style.left = Math.random() * sectionBanner.offsetWidth + `px`;
    //     const figureBackground = figureColors[Math.floor(Math.random() * figureColors.length)];
    //     square.style.background = figureBackground;
    //     square.style.zIndex = "1";
    //     sectionBanner.appendChild(square)


    //     setTimeout(() => {
    //         square.remove()
    //     }, 5000)

    // }

    useEffect(() => {
        // setInterval(createSquare, 150)
        // createCounter();
        // createCounter();
        // return () => clearTimeout(createCounter);
        // const timer = setTimeout(() => {
        //     setCount(count + 1)
        //     console.log(`count`, count)
        //   }, 1000);
        //   return () => clearTimeout(timer);
      }, [])

    const handleMouseMove = (evt) => {
        sceneEl.current.querySelectorAll(`.banners__icon`).forEach((picture) => {
            const speed = picture.getAttribute(`data-speed`);
            console.log(`speed`, speed)
            const x = (document.querySelector(`.banners`).offsetWidth - evt.pageX * speed)/100;
            const y = (document.querySelector(`.banners`).offsetHeight - evt.pageY * speed)/100;
            picture.style.transform = `translateX(${x}px) translateY(${y}px)`
            console.log(`x`, x)
            console.log(`y`, y)
        }

        )
        // console.log(`all svg`, sceneEl.current.querySelectorAll(`.banners__icon`))
    }
    return <>
        <NavigationSite />
        <main className="main">
            <h1 className="visually-hidden">Portfolio site of Alexandra Kuchynskaya</h1>
            {/* <section className="banner">
                <div className="banner__container">
                    <div className="banner__scene">
                        <h3 ref={sceneEl} className="banner__text">
                            <span data-depth-y="2">F</span>
                            <span data-depth-y="-10">R</span>
                            <span data-depth-y="5">O</span>
                            <span data-depth-y="-12">N</span>
                            <span data-depth-y="-8">T</span>
                            <span>-</span>
                            <span data-depth-y="4">E</span>
                            <span data-depth-y="-6">N</span>
                            <span data-depth-y="6">D</span>
                        </h3>
                        <ul className="list banner__list">
                            <li className="banner__item" data-depth-y="-0.5">
                                <img src="/img/001-developer.png"></img>
                            </li>
                        </ul>
                    </div>
                </div>

            </section> */}
            <section className="banners"  onMouseMove={(evt)=> handleMouseMove(evt)}>
                <h2 className="visually-hidden banners__introduction">Banner</h2>
                <div className="bg"></div>
                <div className="bg bg2"></div>
                <div className="bg bg3"></div>
                <div ref={sceneEl}>
                <svg className="banners__icon banners__icon--1" dataDepth="-5" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"  viewBox="0 0 16 16">
                    <path d="M5.854 4.854a.5.5 0 1 0-.708-.708l-3.5 3.5a.5.5 0 0 0 0 .708l3.5 3.5a.5.5 0 0 0 .708-.708L2.707 8l3.147-3.146zm4.292 0a.5.5 0 0 1 .708-.708l3.5 3.5a.5.5 0 0 1 0 .708l-3.5 3.5a.5.5 0 0 1-.708-.708L13.293 8l-3.147-3.146z"/>
                </svg>
                <svg className="banners__icon banners__icon--2" data-speed="5" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                    <path d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27.68 0 1.36.09 2 .27 1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.012 8.012 0 0 0 16 8c0-4.42-3.58-8-8-8z"/>
                </svg>
                <svg className="banners__icon banners__icon--3" data-speed="2" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
                    <path d="M13.5 3a.5.5 0 0 1 .5.5V11H2V3.5a.5.5 0 0 1 .5-.5h11zm-11-1A1.5 1.5 0 0 0 1 3.5V12h14V3.5A1.5 1.5 0 0 0 13.5 2h-11zM0 12.5h16a1.5 1.5 0 0 1-1.5 1.5h-13A1.5 1.5 0 0 1 0 12.5z"/>
