import React, {useRef, useEffect, useState} from 'react';
// import Parallax from 'parallax-js'
import Contact from '../contacts/contsct';
import Footer from '../footer/footer';
import Header from '../header/header';
import ProgressBar from 'react-bootstrap/ProgressBar';
import 'bootstrap/dist/css/bootstrap.min.css';
// import { CircularProgressbar } from 'react-circular-progressbar';


// import 'react-circular-progressbar/dist/styles.css';

const MainPage = () => {
    const [count, setCount] = useState(0);

    // const sceneEl = useRef(null);

    // useEffect(() => {
    //     const parallaxInstance = new Parallax(sceneEl.current, {
    //       relativeInput: true,
    //       hoverOnly: true
    //     })

    //     parallaxInstance.enable();

    //     return () => parallaxInstance.disable();
    //   }, [])
    const createCounter = () => {
        // let count = 0;
        const setFrame = () => {
            setCount(count + 1);
            console.log(`count`, count)
            if (count === 101) {
                clearInterval(runCounter)
            } else {
                return null
            }

        }
        const runCounter = setInterval(setFrame, 50);

    }

    const figureColors = [
        `#3c1874`,
        `#DE354C`,
        `#F3F3F3`,
        `radial-gradient(#de354c, #642493)`,
        `radial-gradient(#eeaeca, #94bbe9)`,
    ]
    const createSquare = () => {
        const sectionBanner = document.querySelector(`.banners`)
        const square = document.createElement(`span`)
        const size = Math.random() * 50;
        square.style.width = `${20 + size}px`;
        square.style.height = `${20 + size}px`;
        square.style.top = Math.random() * sectionBanner.offsetHeight + `px`;
        square.style.left = Math.random() * sectionBanner.offsetWidth + `px`;
        const figureBackground = figureColors[Math.floor(Math.random() * figureColors.length)];
        square.style.background = figureBackground;
        square.style.zIndex = "1";
        sectionBanner.appendChild(square)


        setTimeout(() => {
            square.remove()
        }, 5000)

    }

    useEffect(() => {
        setInterval(createSquare, 150)
        createCounter();
        // createCounter();
        // return () => clearTimeout(createCounter);
        // const timer = setTimeout(() => {
        //     setCount(count + 1)
        //     console.log(`count`, count)
        //   }, 1000);
        //   return () => clearTimeout(timer);
      }, [])

    return <>
        <Header />
        <main className="main">
            <h1 className="visually-hidden">Portfolio site of Alexandra Kuchynskaya</h1>
            {/* <section className="banner">
                <div className="banner__container">
                    <div className="banner__scene">
                        <h3 ref={sceneEl} className="banner__text">
                            <span data-depth-y="2">F</span>
                            <span data-depth-y="-10">R</span>
                            <span data-depth-y="5">O</span>
                            <span data-depth-y="-12">N</span>
                            <span data-depth-y="-8">T</span>
                            <span>-</span>
                            <span data-depth-y="4">E</span>
                            <span data-depth-y="-6">N</span>
                            <span data-depth-y="6">D</span>
                        </h3>
                        <ul className="list banner__list">
                            <li className="banner__item" data-depth-y="-0.5">
                                <img src="/img/001-developer.png"></img>
                            </li>
                        </ul>
                    </div>
                </div>

            </section> */}
            <section className="banners">
                <h2 className="visually-hidden banners__introduction">Banner</h2>
                <div className="banners__wrapper">
                    <p className="banners__intro">Hi, I’m <strong className="banners__name">Alexandra Kuchynskaya</strong>.Nice to meet you. I'm front-end developer.
                    </p>
                    <a className="banners__link">See my works</a>
                </div>
            </section>
            <section className="about">
                <h2 className="title about__title">About me</h2>
                <div>
                    <p>Since beginning my journey as a front-end developer, I've done remote work for different companies and collaborated with talented people to create digital products for both business and consumer use.</p> 

                    <p>Well-organised, hard working, multitasking person. I'm a dedicated professional with scrupulous eye for detail  and a determination to deliver the highest quality.
                    I always try to better myself with every project I work on. I also posses subtle sense of humour and excellent communicative skills.      
                    </p>
                    <p>Fan of any sport activities, yoga and latin dances. Constantly working on self-improvement and personal development. </p>
                    <p>Let's make something special</p>
                    <img src="../../img/my-photo.jpg" width="300" heigth="500" alt="Alexandra Kuchynskaya"/>
                    <a download>Download my resume</a>
                </div>
            </section>
            <section className="skills">
                <div className="chart">
                    <h3>Front-end</h3>
                    <ProgressBar min={0} max={90} now={90} label={`${90}%`} visuallyHidden/>
                    {/* <div>
                        <div className="progress-div" style={{ width: width }}>
                            <div style={{ width: `${value}px` }} className="progress" />
                        </div>
                    </div> */}
                </div>
                <div className="chart">
                    <span>Back-end</span>
                    <div></div>
                </div>
                <div>
                    <ul>
                        <li>
                            <h4>Markup</h4>
                            <ul>
                                <li>HTML</li>
                                <li>CSS</li>
                                <li>PUGjs</li>
                            </ul>
                        </li>
                        <li>
                            <h4>Programming</h4>
                            <ul>
                                <li>JavaScript</li>
                                <li>NodeJs</li>
                                <li>MongoDB</li>
                                <li>MySQL</li>
                                <li>GraphQL</li>
                            </ul>
                        </li>
                        <li>
                            <h4>Frameworks & libraries</h4>
                            <ul>
                                <li>React Js</li>
                                <li>Angular</li>
                                <li>Bootstrap4</li>
                            </ul>
                        </li>
                        <li>
                            <h4>CMS</h4>
                            <ul>
                                <li>WordPress</li>
                                <li>MODx</li>
                                <li>Tilda</li>
                            </ul>
                        </li>
                    </ul>
                </div>
                <div className="skills__diagram">

                </div>

            </section>
            <section className="projects container">
                {/* <h2 className="visually-hidden">Кредиты</h2>
                <div className="credit__container slider__list swiper-wrapper">
                <h3 className="title credit__title slider__item-title slider-item__title">Лига Банк</h3>
                <span className="credit__description slider__item-desc slider-item__desc">Кредиты на любой случай</span>
                <a className="button button__white" href="#">Рассчитать кредит</a>
                </div> */}
            </section>
            <Contact />
        </main>
        <Footer />
    </>
}

export default MainPage;
