import React from 'react';
import {useState} from "react";
import { allNavSiteLinks } from '../const/const';
import Logo from '../logo/logo';
import Navbar from 'react-bootstrap/ProgressBar';

const NavigationSite = () => {
    const defaultActiveLink = ``;
    const [activeLink, setActiveLink] = useState(defaultActiveLink);
    const handleActiveLink = (evt) => {
        setActiveLink(evt.target.dataset.link);
    };
    return <>
        <nav className="header__nav menu container-site">
            <Logo />
            {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button> */}

            <div className="menu__nav">
                <ul className="list grid site-list">
                    {allNavSiteLinks.map((siteLink) => {
                    return <li key={siteLink} className="menu__item  site-list__item">
                        <a href="!#" onClick={handleActiveLink} className={`menu__link site-list__link ${siteLink === activeLink ? `site-list__link--active` : ``}`} data-link={siteLink} >{siteLink}</a>
                    </li>;
                    })}
                </ul>
            </div>
        </nav>
    </>
}

export default NavigationSite;
