import React from 'react';
import {useState} from "react";
import { allNavSiteLinks, MenuNav } from '../const/const';
import Logo from '../logo/logo';
import Navbar from 'react-bootstrap/ProgressBar';
import { ReactComponent as About } from '../../img/person.svg';
import { ReactComponent as Skills } from '../../img/pencil-square.svg';
import { ReactComponent as Interface } from '../../img/laptop.svg';
import { ReactComponent as Projects } from '../../img/image.svg';
import { ReactComponent as Contacts } from '../../img/envelope.svg';


const NavigationSite = () => {
    const defaultActiveLink = ``;
    const [activeLink, setActiveLink] = useState(defaultActiveLink);
    const handleActiveLink = (evt) => {
        setActiveLink(evt.target.dataset.link);
    };
    const returnSvg = (link) => {
        switch (link) {
            case MenuNav.ABOUT:
                return <About /> ;
            case MenuNav.SKILLS:
                return  <Skills />;
            case MenuNav.INTERFACE:
                return <Interface />;
            case MenuNav.PROJECTS:
                return <Projects />;
            case MenuNav.GITHUB:
                return <Contacts />;
            default:
                return ``;
        }
      }
    return <>
        <header className="header">
            <nav className="header__nav menu container">
                <Logo />
                <div className="menu__nav">
                    <ul className="list grid site-list">
                        {allNavSiteLinks.map((siteLink) => {
                        return <li key={siteLink.linkName} className="menu__item  site-list__item">
                            <a href={`#${siteLink.href}`} onClick={handleActiveLink} className={`menu__link site-list__link ${siteLink.linkName === activeLink ? `site-list__link--active` : ``}`} data-link={siteLink.linkName}>{siteLink.linkName}
                            </a>
                        </li>;
                        })}
                    </ul>
                </div>
                <button className="menu__toggle" type="button">
                    <span className="visually-hidden">Open menu</span>
                </button>
                <div>
                {/* <ul className="list social-footer__list">
                    {socialInfo.map((social) => {
                        return <li key={social.name} className="social-footer__item">
                        <a href="!#" className="social-footer__link">
                            <img src={social.src} alt={social.name}></img>
                        </a>
                        </li>;
                    })}
                </ul> */}
                </div>
            </nav>
        </header>
    </>
}

export default NavigationSite;
